roomScripts.0,0,0:
// This is room (0,0,0); the game always starts here.
// We recommend you use this room as a Start Screen
// (i.e. to set default values for your variables, ask the player for anything you need to know - like their name - etc.)
// Then just TELEPORT the player to wherever the real game starts :)

roomName(`Start<br>Screen`);

pl.introDisplayed = false;
pl.keyTaken = false;
pl.hp = 100;
pl.useAdded = false;
addStat(`hp`, `%`);

teleport(`0,0,1`);

roomScripts.0,0,1:
roomName(`Living<br>Room`);

if (pl.introDisplayed === false) {
    pl.introDisplayed = true;
    print(`Welcome to my little exemplar game!<br><br>You need to go out and do some shopping but lost the key to your front door somewhere. Your objective is to find it and get out of the house!`);
}

print(`You are in your living room. To the NORTH is the kitchen. To the EAST and WEST are hallways.`);

addExits(north, east, west);

roomScripts.-3,0,1:
roomName(`Garden`);

print(`You are in your garden. You can see a key lying in the grass.`);

if (pl.keyTaken === false) {
    addRoomObjectAction(`key`, `take`, function() {
        print(`You pick up the key.`);
        removeRoomObject(`key`);
        pl.keyTaken = true;
        addInventoryObjectAction(`key`, `look at`, function() {
            print(`The key to the front door of your house.`);
        });
    });
}

addExits(east);

roomScripts.1,0,1:
print(`You are in a hallway.`);
addExits(east, west);

if (pl.useAdded === true) {
    removeInventoryObjectAction(`key`, `use`);
}

roomScripts.2,0,1:
roomName(`Front<br>Door`);

print(`You are by your front door. If you have the key, you can open it and end the game!`);

addExits(west);

if (pl.keyTaken === true) {
    addInventoryObjectAction(`key`, `use`, function() {
        print(`You use the key to open the door and leave your house. The end!`);
        killGame();
    });
    pl.useAdded = true;
}

roomScripts.0,1,1:
roomName(`Kitchen`);

print(`You are in your kitchen. There is nothing of interest here apart from a very large window you can probably use to get out of the house and see if you dropped the key somewhere nearby.`);

addRoomObjectAction(`window`, `open`, function() {
    print(`You try to open the window, but it's stuck. Of all days!`);
});

addExits(south);
